mapper.insert:
INSERT INTO INSA(sabun, 
				join_day, 
				retire_day, 
				(SELECT code FROM PUB WHERE gubun='FF') AS put_yn, 
				name, 
				reg_no, 
				eng_name, 
				phone, 
				hp, 
				carrier, 
				(SELECT code FROM PUB WHERE gubun='AA') AS pos_gbn_code,
				cmp_reg_no, 
				cmp_reg_image, 
				sex, 
				years, 
				email, 
				zip, 
				addr1, 
				addr2,
				(SELECT code FROM PUB WHERE gubun='BB') AS dept_code, 
				(SELECT code FROM PUB WHERE gubun='CC') AS join_gbn_code,
				id, 
				pwd,
				salary, 
				(SELECT code FROM PUB WHERE gubun='EE') AS kosa_reg_yn, 
				(SELECT code FROM PUB WHERE gubun='KS') AS kosa_class_code, 
				(SELECT code FROM PUB WHERE gubun='DD') AS mil_yn, 
				(SELECT code FROM PUB WHERE gubun='MT') AS mil_type, 
				(SELECT code FROM PUB WHERE gubun='ML') AS mil_level, 
				mil_startdate, 
				mil_enddate, 
				(SELECT code FROM PUB WHERE gubun='GG') AS join_type, 
				(SELECT code FROM PUB WHERE gubun='HH') AS gart_level, 
				self_intro, 
				crm_name, 
				profile_image, 
				carrier_image
				) 
		VALUES(#{sabun}
			, #{join_day}
			, #{retire_day}
			, #{put_yn}
			, #{name}
			, #{reg_no}
			, #{eng_name}
			, #{phone}
			, #{hp}
			, #{carrier}
			, #{pos_gbn_code}
			, #{cmp_reg_no}
			, #{cmp_reg_image}
			, #{sex}
			, #{years}
			, #{email}
			, #{zip}
			, #{addr1}
			, #{addr2}
			, #{dept_code}
			, #{join_gbn_code}
			, #{id}
			, #{pwd}
			, #{salary}
			, #{kosa_reg_yn}
			, #{kosa_class_code}
			, #{mil_yn}
			, #{mil_type}
			, #{mil_level}
			, #{mil_startdate}
			, #{mil_enddate}
			, #{join_type}
			, #{gart_level}
			, #{self_intro}
			, #{crm_name}
			, #{profile_image}
			, #{carrier_image}
			)

mapper.select:
SELECT sabun, name, reg_no, hp, (SELECT P.gubun FROM PUB p, INSA i WHERE gubun='AA' AND p.code=i.pos_gbn_code) pos_gbn_code, join_day, retire_day, put_yn, salary FROM INSA ORDER BY sabun
name like '%' || #{name} || '%'
AND pos_gbn_code=#{pos_gbn_code}
AND join_day like '%' || #{join_day} || '%'
AND retire_day like '%' ||#{retire_day} || '%'
AND join_gbn_code=#{join_gbn_code}
